package com.yeepay.g3.facade.nccashier.error;import java.util.HashMap;import java.util.Map;import com.yeepay.g3.utils.common.StringUtils;/** * FE错误码对应关系 * @author tanzhen * @date 2016-08-10 */public class FEErrorCodeMapping {	private static final String DEFAULT = "default";	/**	 * FE错误码对应关系	 */	public static final Map<String, String> ErrorCodeMap = new HashMap<String, String>();	/**     * 系统异常     */    public static final String F0001000 = "F0001000";    /**     * 参数不合法     */    public static final String F0001001 = "F0001001";    /**     * 重复请求     */    public static final String F0001002 = "F0001002";    /**     * 非法请求     */    public static final String F0001003 = "F0001003";    /**     * 请求超时     */    public static final String F0001004 = "F0001004";        /**     * 底层系统抛YeepayRuntimeException     */    public static final String F0001005 = "F0001005";    /**     * 支付次数超过限制（用户可见）     */    public static final String F0001006 = "F0001006";            /**     * 订单已经失败     */    public static final String F0002000 = "F0002000";    /**     * 订单已经成功     */    public static final String F0002001 = "F0002001";    /**     * 订单正在支付中     */    public static final String F0002002 = "F0002002";    /**     * 订单已经失效     */    public static final String F0002003 = "F0002003";    /**     * 订单不存在     */    public static final String F0002004 = "F0002004";    /**     * 订单状态不合法     */    public static final String F0002005 = "F0002005";            /**     * 调用mpay旧版微信子系统失败     */    public static final String F0003001 = "F0003001";        /**     * 调用银行子系统失败     */    public static final String F0003002 = "F0003002";        /**     * 发送mq消息失败     */    public static final String F0003004 = "F0003004";        /**     * mpay:请求参数为空，具体为空的请求参数     */    public static final String F0005001 = "F0005001";        /**     * mpay:请求参数格式不正确，具体格式不正确的参数     */    public static final String F0005002 = "F0005002";        /**     * mpay:验证签名失败(微信交互签名错误)     */    public static final String F0005003 = "F0005003";        /**     * mpay:请求微信结果失败(具体失败信息)     */    public static final String F0005004 = "F0005004";        /**     * mpay:微信通信异常     */    public static final String F0005005 = "F0005005";        /**     * mpay:微信参数错误(具体微信返回的某个错误参数)     */    public static final String F0005006 = "F0005006";        /**     * mpay:商户未设置子商户编号(数据表中子商户编号为空)     */    public static final String F0005007 = "F0005007";        /**     * mpay:微信商户未注册(未通过OP备案)     */    public static final String F0005008 = "F0005008";        /**     * mpay:jsapi支付没有设置appId(数据表中appId为空)     */    public static final String F0005009 = "F0005009";        /**     * bank:其他（未知的错误码）     */    public static final String F0005010 = "F0005010";        /**     * bank:平台代码无效     */    public static final String F0005011 = "F0005011";        /**     * bank:缺少必填项的输入     */    public static final String F0005012 = "F0005012";        /**     * bank:不支持的银行     */    public static final String F0005013 = "F00050013";        /**     * bank:没有可用的通道     */    public static final String F0005014 = "F0005014";        /**     * bank:银行状态异常     */    public static final String F0005015 = "F0005015";        /**     * bank:银行编码有误     */    public static final String F0005016 = "F0005016";        /**     * bank:重复的请求     */    public static final String F0005017 = "F0005017";        /**     * bank:原交易订单状态不支持此请求     */    public static final String F0005018 = "F0005018";        /**     * bank:非法请求     */    public static final String F0005019 = "F0005019";        /**     * bank:金额不能为空或者格式有误     */    public static final String F0005020 = "F0005020";        /**     * bank:校验项有误     */    public static final String F0005021 = "F0005021";        /**     * bank:银行处理中,请勿重复操作     */    public static final String F0005022 = "F0005022";        /**     * bank:系统异常,请勿重复操作     */    public static final String F0005023 = "F0005023";        /**     * bank:持卡人金额超限，超过通道限额     */    public static final String F0005024 = "F0005024";        /**     * bank:交易要素非法或不能为空     */    public static final String F0005025 = "F0005025";        /**     * bank:银行流程校验失败     */    public static final String F0005026 = "F0005026";        /**     * bank:金额不能低于1元     */    public static final String F0005027 = "F0005027";        /**     * bank:超过银行笔数限制     */    public static final String F0005028 = "F0005028";        /**     * bank:银行交易关闭     */    public static final String F0005029 = "F0005029";        /**     * bank:原交易未找到或者已经处理     */    public static final String F0005030 = "F0005030";        /**     * bank:超过协议签约/撤销次数限制     */    public static final String F0005031 = "F0005031";        /**     * bank:交易超时     */    public static final String F0005032 = "F0005032";            /**     * bank:请求威富通报错，未知错误     */    public static final String F0005033 = "F0005033";    	/**	 * 对应关系	 */	static {		ErrorCodeMap.put("default","3500003");		ErrorCodeMap.put("F0001001","3500004");		ErrorCodeMap.put("F0001002","3500005");		ErrorCodeMap.put("F0001003","3500006");		ErrorCodeMap.put("F0001004","3500007");		ErrorCodeMap.put("F0001006","3500015");				ErrorCodeMap.put("F0002000","3500009");		ErrorCodeMap.put("F0002001","3500010");		ErrorCodeMap.put("F0002002","3500011");		ErrorCodeMap.put("F0002003","3500012");		ErrorCodeMap.put("F0002004","3500013");		ErrorCodeMap.put("F0002005","3500014");	}	public static String translateCode(String code) {		if(StringUtils.isEmpty(code)){			return code;		}				String errorCode = ErrorCodeMap.get(code);		if (StringUtils.isEmpty(errorCode)) {			if(code.substring(0, 1).equals("6")){				errorCode=code;			}else{				errorCode = ErrorCodeMap.get(DEFAULT);			}		}		return errorCode;	}}